import { useSelector } from 'react-redux';

export const columns = [
  {
    title: 'Number',
    dataIndex: 'number',
    key: 'number',
    datatype: 'string',
  },
  {
    title: 'Date',
    dataIndex: 'date',
    key: 'date',
    datatype: 'date',
  },
  {
    title: 'Payee',
    dataIndex: 'disbursement',
    key: 'disbursement',
    datatype: 'object',
    dataToString: (object) => object.payee.name,
  },
];

const FormDetails = () => {
  const pdcDisbursements = useSelector((state) => state.accounting.PDCDisbursements.list);

  const formDetails = {
    form_name: 'pdc_vouchers',
    form_items: [
      {
        label: 'PDC Voucher Number',
        name: 'number',
        placeholder: 'AUTOGENERATED UPON CREATION',
        readOnly: true,
      },
      {
        label: 'Date',
        name: 'date',
        type: 'date',
        rules: [{ required: true }],
      },
      {
        label: 'PDC Disbursement',
        name: 'disbursement',
        type: 'selectSearch',
        selectName: 'name',
        choices: pdcDisbursements,
        render: (data) => `${data.number}`,
        rules: [{ required: true }],
      },
    ],
  };

  return { formDetails };
};

export default FormDetails;