import { useState } from 'react';
import { useSelector } from 'react-redux';
import moment from 'moment';

export const columns = [
  {
    title: 'Number',
    dataIndex: 'number',
    key: 'number',
    datatype: 'string',
  },
  {
    title: 'Date',
    dataIndex: 'date',
    key: 'date',
    datatype: 'date',
  },
  {
    title: 'Payee',
    dataIndex: 'disbursement',
    key: 'disbursement',
    datatype: 'object',
    dataToString: (object) => object.payee.name,
  },
];

const FormDetails = () => {
  const pdcDisbursements = useSelector((state) => state.accounting.PDCDisbursements.list);
  const [displayModal, setDisplayModal] = useState(false);

  const formDetails = {
    form_name: 'pdc_vouchers',
    form_items: [
      {
        label: 'PDC Voucher Number',
        name: 'number',
        placeholder: 'AUTOGENERATED UPON CREATION',
        readOnly: true,
      },
      {
        label: 'Date',
        name: 'date',
        type: 'date',
        rules: [{ required: true }],
      },
      {
        label: 'PDC Disbursement',
        name: 'disbursement',
        type: 'selectTable',
        rules: [{ required: true }],
        allowEmpty: true,
        placeholder: 'Select DR/OS',
        displayModal,
        setDisplayModal,
        dataSource: pdcDisbursements,
        columns: [
          {
            title: 'PDC Number',
            dataIndex: 'number',
            key: 'number',
          },
          {
            title: 'Date',
            dataIndex: 'date',
            key: 'date',
            render: (date) => moment(new Date(date)).format('DD/MM/YYYY'),
          },
          {
            title: 'Payee',
            dataIndex: 'payee',
            key: 'payee',
            render: (data) => `[${data?.payee?.code}] ${data?.payee?.name}`
          },
        ],
        rowKey: 'id',
        getValueProps: (value) => {
          if (typeof value !== 'undefined') {
            return { value };
          }
        },
      },
    ],
  };

  const tableDetails = {
    label: 'Cheques',
    name: 'cheques',
    key: 'cheques',
    rules: [{ required: true }],
    fields: [
      {
        label: 'Cheque Number',
        name: 'number',
      },
      {
        label: 'Amount',
        name: 'amount',
      },
      {
        label: 'Remarks',
        name: 'remarks',
      },
    ],
  };

  return { formDetails, tableDetails };
};

export default FormDetails;