import { useDispatch, useSelector } from 'react-redux';
import { useState } from 'react';
import moment from 'moment';

import { listProductInventoryWithStockByDepot, clearData as clearPI } from '../../ProductInventories/redux';

export const columns = [
  {
    title: 'FG-IS Number',
    dataIndex: 'pisNo',
    key: 'pisNo',
    datatype: 'string',
  },
  {
    title: 'Date',
    dataIndex: 'date',
    key: 'date',
    datatype: 'date',
  },
  {
    title: 'Ship From',
    dataIndex: 'fromDepot',
    key: 'fromDepot',
    datatype: 'object',
    dataToString: (object) => {
      return `[${object.code}] ${object.name}`;
    },
  },
  {
    title: 'Ship To',
    dataIndex: 'toDepot',
    key: 'toDepot',
    datatype: 'object',
    dataToString: (object) => {
      return `[${object.code}] ${object.name}`;
    },
  },
  {
    title: 'Requested By',
    dataIndex: 'requestedBy',
    key: 'requestedBy',
    datatype: 'object',
    dataToString: (object) => {
      return `${object.firstName} ${object.lastName}`;
    },
  },
  {
    title: 'Status',
    dataIndex: 'status',
    key: 'status',
    datatype: 'status',
  },
];

const FormDetails = () => {
  const dispatch = useDispatch();
  const depots = useSelector((state) => state.maintenance.depots.list);
  const company = useSelector((state) => state.company.selectedCompany);
  const productInventories = useSelector((state) => state.dashboard.productInventories.list);
  const [loadingDepot, setLoadingDepot] = useState(false);

  const formDetails = {
    form_name: 'fg_issuance',
    form_items: [
      {
        label: 'FS-IS Number',
        name: 'pisNo',
        placeholder: 'AUTOGENERATED UPON CREATION',
        readOnly: true,
      },
      {
        label: 'Date',
        name: 'date',
        type: 'date',
        rules: [{ required: true }],
      },
      {
        label: 'Ship from',
        name: 'fromDepot',
        type: 'selectSearch',
        selectName: 'name',
        choices: depots,
        render: (depot) => `[${depot.code}] ${depot.name}`,
        rules: [{ required: true }],
        loading: loadingDepot,
        onChange: (e) => {
          setLoadingDepot(true)
          dispatch(clearPI());
          dispatch(listProductInventoryWithStockByDepot({ company, depot: e })).then(() => {
            setLoadingDepot(false)
          })
        },
      },
      {
        label: 'Ship to',
        name: 'toDepot',
        type: 'selectSearch',
        selectName: 'name',
        choices: depots,
        render: (depot) => `[${depot.code}] ${depot.name}`,
        rules: [{ required: true }],
      },
      {
        label: 'Remarks',
        name: 'remarks',
        type: 'textArea',
        rules: [{ message: 'Please provide a valid remark' }],
        placeholder: 'Remarks',
      },
    ],
  };

  const tableDetails = {
    label: 'Inventory List',
    name: 'inventoryList',
    key: 'inventoryList',
    rules: [{ required: true }],
    isVisible: productInventories.length > 0,
    fields: [
      {
        label: 'Lot Number',
        name: 'product',
        render: (object) => {
          return object.product.lotNumber;
        },
      },
      {
        label: 'Finished Good',
        name: 'product',
        render: (object) => {
          return object.product.finishedGood.name;
        },
      },
      {
        label: 'Stock on Hand',
        name: 'stockOnHand',
        type: 'readOnly',
      },
      {
        label: 'Quantity Issued',
        name: 'quantity',
        type: 'number',
        rules: [{ required: true }],
        min: 0,
      },
      {
        label: 'Quantity Remaining',
        name: 'quantityRemaining',
        render: (object) => {
          if (object.stockOnHand !== null && typeof object.stockOnHand !== 'undefined') {
            return object.stockOnHand - object.quantity || object.stockOnHand;
          }
        },
      },
    ],
    foreignKey: 'id',
    selectedKey: 'id',
    selectData: productInventories,
    selectFields: [
      {
        title: 'Lot Number',
        dataIndex: 'product',
        key: 'product',
        datatype: 'object',
        dataToString: (product) => {
          return product.lotNumber;
        },
        render: (product) => {
          return product.lotNumber;
        },
      },
      {
        title: 'Date Created',
        dataIndex: 'dateCreated',
        key: 'dateCreated',
        datatype: 'date',
        render: (object) => {
          return moment(new Date(object)).format('DD/MM/YYYY');
        },
      },
      {
        title: 'FG Code',
        dataIndex: 'product',
        key: 'product',
        datatype: 'object',
        dataToString: (object) => {
          return `[${object.finishedGood.code}] ${object.finishedGood.name}`;
        },
        render: (object) => {
          return `[${object.finishedGood.code}] ${object.finishedGood.name}`;
        },
      },
      {
        title: 'Expiration',
        dataIndex: 'product',
        key: 'product',
      },
      {
        title: 'Stock',
        dataIndex: 'quantity',
        key: 'quantity',
      },
    ],
    getValues: (values) => {
      const products = [];
      values.returnSlipProducts.forEach((product) => {
        products.push({
          ...product,
        });
      });
      return products;
    },
    processData: (data) => {
      const processedData = {
        ...data,
        product: data.product,
        key: data.product.id,
        stockOnHand: data.quantity,
      };
      delete processedData.quantity;
      return processedData;
    },
    checkSelected: (selectedData, rowData) => {
      if (
        typeof selectedData !== 'undefined' &&
        selectedData !== null &&
        selectedData.some((item) => item.product.id === rowData.product.id)
      ) {
        return true;
      }
    },
    renderTableColumns: (fields) => {
      const columns = [];
      fields.forEach((field) => {
        if (typeof field.render === 'undefined' || field.render === null) {
          field.render = (object) => object[field.name];
        }
        if (field.name !== 'stockOnHand' && field.name !== 'quantityRemaining') {
          columns.push({
            title: field.label,
            key: field.name,
            render: (object) => field.render(object),
          });
        }
      });

      return columns;
    },
  };

  return { formDetails, tableDetails };
};

export default FormDetails;
