
import { useSelector } from 'react-redux';
import Helper from '../helpers'

export const DisplayDetails = () => {
    const departments = useSelector((state) => state.maintenance.departmentArea.deptList);

    const columns = [
        {
            title: 'PRF Number',
            dataIndex: 'number',
            key: 'number',
            sorter: (a, b) => a.id - b.id,
        },
        {
            title: 'PRF Date',
            dataIndex: 'date',
            key: 'date',
            datatype: 'date',
        },
        {
            title: 'Date Needed',
            dataIndex: 'dateNeeded',
            key: 'dateNeeded',
            datatype: 'date',
        },
        {
            title: 'Department',
            dataIndex: 'department',
            key: 'department',
            filters: departments,
            filterKey: 'name',
            datatype: 'object',
            dataToString: (department) => {
              return department.name
            }
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            datatype: 'string',
        },
    ];

    const itemColumns = [
        {
            title: 'Type',
            dataIndex: 'item',
            key: 'item',
            render: (object) => object.type.name,
        },
        {
            title: 'Code',
            dataIndex: 'item',
            key: 'item',
            render: (object) => object.code,
        },
        {
            title: 'Name',
            dataIndex: 'item',
            key: 'item',
            render: (object) => object.name,
        },
        {
            title: 'Unit',
            dataIndex: 'item',
            key: 'item',
            render: (object) => object.unit.name,
        },
        {
            title: 'Current Stocks',
            dataIndex: 'stocks',
            key: 'stocks',
        },
        {
            title: 'Quantity Requested',
            dataIndex: 'quantityRequested',
            key: 'quantityRequested',
        },
    ];

    return { columns, itemColumns };

}

export const FormDetails = () => {
    const items = useSelector((state) => state.maintenance.items.list); 
    const departments = useSelector((state) => state.maintenance.departmentArea.deptList);
    
    const helper = Helper()
    
    const formDetails = {
      form_name: 'purchase_request',
      form_items: [
        {
          label: 'PRF Number',
          name: 'number',
          placeholder: 'AUTOGENERATED UPON CREATION',
          readOnly: true,
        },
        {
          label: 'PRF Date',
          name: 'date',
          type: 'date',
          rules: [{ required: true }],
        },
        {
          label: 'Date Needed',
          name: 'dateNeeded',
          type: 'date',
          rules: [{ required: true }],
        },
        {
          label: 'Department',
          name: 'department',
          type: 'selectSearch',
          selectName: 'name',
          choices: departments,
          render: (department) => `[${department.code}] ${department.name}`,
          rules: [{ required: true }],
        },
        {
          label: 'Remarks',
          name: 'remarks',
          type: 'textArea',
          rules: [{ message: 'Please provide a valid remark' }],
          placeholder: 'Remarks',
        },
      ],
    };
  
    const tableDetails = {
        label: 'Requested Items',
        name: 'requestedItems',
        key: 'id',
        rules: [{ required: true }],
        fields: [
            {
                label: 'Item ID',
                name: 'itemID',
                type: 'hidden'
            },
            {
                label: 'Item Name',
                name: 'name',
            },
            {
                label: 'Item Name',
                name: 'name',
            },
            {
                label: 'Code',
                name: 'code',
            },
            {
                label: 'Type',
                name: 'type',
                render: (object) => object.name,
            },
            {
                label: 'Unit of Measurement',
                name: 'unit',
                render: (object) => object.name,
            },
            /*
            {
                title: 'Current Stocks',
                dataIndex: 'stocks',
                key: 'stocks',   
            },
            {
                title: 'Pending PR',
                dataIndex: 'purchase_request',
                key: 'purchase_request',   
            },
            {
                title: 'Pending PO',
                dataIndex: 'purchase_order',
                key: 'purchase_order',   
            },
            {
                title: 'Quarantined',
                dataIndex: 'quarantined',
                key: 'quarantined',   
            }
            */
            {
                label: 'Quantity Requested',
                name: 'quantityRequested',
                type: 'number',
                rules: [{ required: true }],
                min: 0,
            },
        ],
        foreignKey: 'itemID',
        selectedKey: 'id',
        selectData: items,
        selectFields: [
            {
                title: 'Item Name',
                dataIndex: 'name',
                key: 'name',
            },
            {
                title: 'Code',
                dataIndex: 'code',
                key: 'code',
            },
            {
                title: 'Type',
                dataIndex: 'type',
                key: 'type',
                render: (object) => object.name,
            },
            {
                title: 'Unit of Measurement',
                dataIndex: 'unit',
                key: 'unit',
                render: (object) => object.name,
            },
            /*{
                title: 'PRF Quantity',
                dataIndex: 'requestedQuantity',
                key: 'requestedQuantity',
            },
            {
                title: 'PO Quantity',
                dataIndex: 'orderedQuantity',
                key: 'orderedQuantity',
            },*/
        ],
        processData: (data) => {
            var processedData = {
                ...data,
                itemID: data.id
            }
            delete processedData.id;
            return processedData
        },
        checkSelected: (selectedData, rowData) => {
            console.log(selectedData)
            console.log(rowData)
            if (
                typeof selectedData !== 'undefined' &&
                selectedData !== null &&
                selectedData.some((item) => item.itemID === rowData.id)
            ) {
                return true;
            }
        },
    };
  
    return { formDetails, tableDetails };
  };